    //Ghi giá trị đảo để hiển thị LED
    led_value ^= 0x01;
    led_value ^= 0x01;
    WRITE_PERI_REG(RTC_GPIO_OUT, (READ_PERI_REG(RTC_GPIO_OUT) & (uint32_t)0xfffffffe) | (uint32_t)(led_value & 1));
